import React from "react";
import axios from "axios";
import {
  message,
  Button,
  Checkbox,
  Input,
  Form,
  Card,
  Affix,
  Image,
} from "antd";
import Mail from "./Mail";
const { Meta } = Card;
const renderStyle = {
  listStyle: "none",
  width: "30%",
};
const plainOptions = ["Apple", "Pear", "Orange"];

class GithubSelect extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      arr: [],
      loading: false,
    };
  }
  render() {
    const { loading } = this.state;
    return (
      <div style={{ backgroundColor: "#fff",width:"100%" }}>
        <Affix onChange={(affixed) => console.log(affixed)}>
          <div>
            <Form
              name="horizontal_login"
              layout="inline"
              onFinish={(values) => {
                this.setState({ loading: true });
                const { key } = values;
                axios
                  .get(`http://localhost:8888/api/gitselect?name=${key}`)
                  .then((res) => {
                    if(res.data.length==0){
                      message.error("数据为空！")
                    }
                    if (res.status == 200) {
                      const { data } = res;
                      this.setState({ arr: data, loading: false });
                    } else {
                      this.setState({ arr: [], loading: false });
                    }
                  });
              }}
            >
              <Form.Item
                label="输入关键字"
                name="key"
                rules={[
                  {
                    required: true,
                    message: "Please input your username!",
                  },
                ]}
              >
                <Input
                // style={{ width: '20%' }}
                />
              </Form.Item>
              <Form.Item>
                <Button type="primary" htmlType="submit">
                  搜索
                </Button>
              </Form.Item>
            </Form>
          </div>
        </Affix>
        <br />
        <br />
        <div
          style={{
            width: "77%",
            height:"400px",
            overflowY:'auto'
          }}
        >
          {this.state.arr.map((item) => (
            <Card
              loading={loading}
              style={{
                float: "left",
                width: 240,
                height:337,
              }}
              key={item.id}
              hoverable
              cover={
                <Image
                  // width={200}
                  fallback={"data:image/png;base64,"}
                  src={item.avatar_url||error}
                />
              }
            >
              <Meta title={item.login} description={item.html_url} />
            </Card>
          ))}
        </div>
      </div>
    );
  }
}

class TestAnimation extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      arr: [],
    };
  }
  handleQuery = () => {
    axios.get("http://localhost:8888/api/zhihu").then((res) => {
      if (res.status == 200) {
        this.setState({ arr: res.data }, () => {
          message.success("获取数据完成");
        });
      }
    });
  };
  resetHandle = () => {
    this.setState({ arr: [] }, () => {
      message.success("重置成功");
    });
  };
  onChange = (value) => {
    console.log(value);
  };
  render() {
    return (
      <div>
        <Mail></Mail>
        <br />
        <div>测试反向代理</div>
        <Button onClick={this.handleQuery} type="primary">
          点击
        </Button>
        <Button onClick={this.resetHandle} type="ghost">
          重置
        </Button>
        <ul style={renderStyle}>
          <li>
            <h1>知乎热搜</h1>
          </li>
          {this.state.arr.map((item, index) => {
            return <li key={item.uuid}>{item.query}</li>;
          })}
        </ul>
        <GithubSelect></GithubSelect>
      </div>
    );
  }
}

export default TestAnimation;
